package ctci;

import java.util.Stack;

public class CountBinarySubstringsRETRY_WorksButLimitExceeded {
	
	public static void main(String[] args) {
		String s = "";
		int count = 0;
		
		for(int i = 0; i < s.length() - 1; i++) {
			Stack<Character> a = new Stack<Character>();
			Stack<Character> b = new Stack<Character>();
			a.push(s.charAt(i));
			int pos = 1;
			while((i + pos) < s.length() && a.peek() == s.charAt(i + pos)) {
				a.push(s.charAt(i + pos));
				pos++;
			}
			
			if(i+pos < s.length() ) {
				b.push(s.charAt(i+pos));
				pos++;
			}
			
			while((i + pos) < s.length() && b.peek() == s.charAt(i+pos) && b.size() < a.size()) {
				b.push(s.charAt(i + pos));
				pos++;
			}
			if(a.size() == b.size()) {
				count++;
			}
			System.out.println("i: " + i);
			System.out.println("pos: " + pos);

		}
		System.out.println(count);
	}

}
